var Coords = {};


module.exports = Coords;


Coords.Quad = {
	I: {
		X: 1,
		Y: 1
	},
	II: {
		X: -1,
		Y: 1
	},
	III: {
		X: -1,
		Y: -1
	},
	IV: {
		X: 1,
		Y: -1
	}
};


Coords.getPointQuadrant = function (point) {
	var map = ['I', 'II', 'IV', 'III'];

	var x = Math.sign(point.x) >= 0 ? 0 : 1;
	var y = Math.sign(point.y) >= 0 ? 0 : 1;

	return Coords.Quad[map[(y << 1) | x]];
};
