module.exports = Point;


function Point(x, y) {
	this.x = x || 0;
	this.y = y || 0;
}

Point.between = function(a, b) {
	return new Point(
		(a.x + b.x) / 2,
		(a.y + b.y) / 2
	);
};

Point.fromEventClientXY = function (event) {
	return new Point(event.clientX, event.clientY);
};

Point.prototype.add = function (p) {
	return new Point(this.x + p.x, this.y + p.y);
};

Point.prototype.sub = function (p) {
	return new Point(this.x - p.x, this.y - p.y);
};

Point.prototype.mul = function (x) {
	return new Point(this.x * x, this.y * x);
};

Point.prototype.div = function (x) {
	return new Point(this.x / x, this.y / x);
};

Point.prototype.abs = function () {
	return new Point(Math.abs(this.x), Math.abs(this.y));
};

Point.prototype.getTgDir = function () {
	return Math.sign(Math.sign(this.x) * Math.sign(this.y)) || 1;
};
